<script>
	/**
	 * vuex管理登陆状态，具体可以参考官方登陆模板示例
	 */
	import {
		mapMutations
	} from 'vuex';
	export default {
		methods: {
			async init(){
				// https://s1.ax1x.com/2020/04/07/G2usUI.png
				// https://s1.ax1x.com/2020/04/07/G2uavD.png
				// https://s1.ax1x.com/2020/04/07/G2urVA.png
				// https://s1.ax1x.com/2020/04/07/G2uBbd.png
				// https://s1.ax1x.com/2020/04/07/G2u0DH.png
				// var [err,res1] =  await uni.getLocation({
				//     type: 'wgs84'
				// })
				var res1={}
				res1.errMsg ='getLocation:ok'
				res1.longitude ='11.22'
				res1.latitude ='11.22'
				if(res1.errMsg  === 'getLocation:ok'){
					uni.showLoading({
						title:"加载中..."
					})
					this.$api.request('get','storage/position/getRecentlyStorage',{
						lng:res1.longitude,
						lat:res1.latitude
					},failres => {
						uni.hideLoading()
					}).then(res=>{
						uni.hideLoading()
						this.$store.commit('setStorage',res.data.id)
						this.$store.commit('setStorageObj',{businessStartTime:"9:30",
							businessState:false,
							businessStopTime:"22:30",
							deliveryStartTime:"9:30",
							deliveryStopTime:"23:00",
							haveStorage:true,
							id:res.data.id}
						)
					})
				}
				if(this.$store.state.userInfo.accessToken && this.$store.state.storageId != 0){
					this.$api.request('get','cart/app/countCart',{
					storageId:this.$store.state.storageId
				}).then(res=>{
						if(res.data > 0){
							uni.setTabBarBadge({
								index:2,
								text:res.data+''
							})
						}
						this.$store.commit('addCart',res.data)
					}).catch(err=>{
						this.$api.msg('请求失败，请稍后再试')
					})
				}
			},
			...mapMutations(['login'])
		},
		onLaunch: function() {
			let userInfo = uni.getStorageSync('userInfo') || '';
			if(userInfo.id){
				//更新登陆状态
				uni.getStorage({
					key: 'userInfo',
					success: (res) => {
						this.login(res.data);
					}
				});
			}
			//全局获取仓库信息
			this.init()
		},
		onShow: function() {
			console.log('App Show')
		},
		onHide: function() {
			console.log('App Hide')
		},
	}
</script>

