SELECT id, order_id AS orderId, `status`, pay_price AS payPrice, create_time AS createTime FROM kx_store_order WHERE uid = #{userId}
and `status` in
#{item}
ORDER BY id DESC LIMIT #{offset}, #{limit}

SELECT tt.cate_id AS categoryId, tt.title AS categoryTitle, ifnull( sum( price ), 0 ) AS totalSalesVolume, count( 1 ) AS totalSales FROM ( SELECT spu.cate_id, t.pid, t.title AS cate_name, o.store_id, os.product_attr_id, os.product_id, os.product_title AS title, os.price FROM kx_store_order_product os LEFT JOIN kx_store_order o ON o.order_id = os.order_no LEFT JOIN kx_store_product spu ON os.product_id = spu.id LEFT JOIN ( SELECT c.id, c.pid, c.cate_name AS title FROM `kx_store_category` c WHERE c.pid = 0 ) t ON spu.cate_id = t.id WHERE o.STATUS IN ( 14, 16, 20, 30, 32, 34, 40, 50 ) AND TO_DAYS( o.update_time ) = TO_DAYS( NOW( ) )
AND o.store_id = #{storageId}
and o.store_id in
#{list}
) tt GROUP BY tt.pid, tt.title, tt.product_id ORDER BY totalSalesVolume, totalSales DESC LIMIT 10

SELECT tt.cate_id AS categoryId, tt.title AS categoryTitle, ifnull( sum( price ), 0 ) AS totalSalesVolume, count( 1 ) AS totalSales FROM ( SELECT spu.cate_id, t.pid, t.title AS cate_name, o.store_id, os.product_attr_id, os.product_id, os.product_title AS title, os.price FROM kx_store_order_product os LEFT JOIN kx_store_order o ON o.order_id = os.order_no LEFT JOIN kx_store_product spu ON os.product_id = spu.id LEFT JOIN ( SELECT c.id, c.pid, c.cate_name AS title FROM `kx_store_category` c WHERE c.pid = 0 ) t ON spu.cate_id = t.id WHERE o.STATUS IN ( 14, 16, 20, 30, 32, 34, 40, 50 ) AND TO_DAYS( o.update_time ) = TO_DAYS( NOW( ) )
AND o.store_id = #{storageId}
and o.store_id in
#{list}
) tt GROUP BY tt.pid, tt.title, tt.product_id, tt.product_attr_id, tt.cate_name, tt.price ORDER BY totalSalesVolume, totalSales DESC LIMIT 10

