mapper.select:
select sto.name as 'storageName',sto.id as 'storageId',ca.cate_name as 'categoryName',sp.bar_code as 'barCode',
sp.id as 'productId',sp.id as 'productAttrId',sp.store_name as 'productName',sp.store_name as 'productAttrName',(st.frezz_stock+st.stock) as 'nowStock',
st.stock as 'stock',st.frezz_stock as 'frezzStock',st.warning_num as 'warningNum',sp.unit_name as 'unitName'
from kx_stock st
left join kx_store_product sp on sp.id = st.product_id
LEFT JOIN kx_storage sto on sto.id = st.storage_id
LEFT JOIN kx_store_category ca on ca.id = sp.cate_id
where st.status = 1

    and st.storage_id = #{bo.storageId}


    and sp.cate_id = #{bo.categoryId}


    and sp.cate_id in

        #{list}



    and ( sp.id like CONCAT('%',#{bo.name},'%') or sp.title like CONCAT('%',#{bo.name},'%')  or sk.bar_code like CONCAT('%',#{bo.name},'%') )



        and (st.frezz_stock + st.stock) >= ${bo.minNum}

        and  (st.frezz_stock + st.stock) <= ${bo.maxNum}



        and st.stock >= ${bo.minNum}

        and  st.stock <= ${bo.maxNum}



        and st.frezz_stock >= ${minNum}

        and  st.frezz_stock <= ${maxNum}





        and st.stock <= st.warning_num



    and st.storage_id in

        #{list}



    limit #{offset},#{size}

select count(st.id)
from kx_stock st
left join kx_store_product sp on sp.id = st.product_id
LEFT JOIN kx_storage sto on sto.id = st.storage_id
LEFT JOIN kx_store_category ca on ca.id = sp.cate_id
where st.status = 1

    and st.storage_id = #{bo.storageId}


    and sp.cate_id = #{bo.categoryId}


    and sp.cate_id in

        #{list}



    and ( sp.id like CONCAT('%',#{bo.name},'%') or sp.title like CONCAT('%',#{bo.name},'%')  or sk.bar_code like CONCAT('%',#{bo.name},'%') )



        and (st.frezz_stock + st.stock) >= ${bo.minNum}

        and  (st.frezz_stock + st.stock) <= ${bo.maxNum}



        and st.stock >= ${bo.minNum}

        and  st.stock <= ${bo.maxNum}



        and st.frezz_stock >= ${minNum}

        and  st.frezz_stock <= ${maxNum}





        and st.stock <= st.warning_num



    and st.storage_id in

        #{list}

SELECT s.id, s.storage_id as storageId, sto.name as storageName, ca.cate_name as categoryName, ca.id as categoryId, spu.bar_code as barCode, s.product_id as productId, s.product_attr_id as productAttrId, s.frezz_stock as frezzStock, spu.store_name as productName, spu.store_name as productAttrName, s.status as status, s.stock as stock, s.sales, spu.unit_name as unitName, spu.ot_price as originalPrice, s.price as price, s.update_time as updateTime, s.create_time as createTime FROM kx_stock s inner JOIN kx_store_product spu on s.product_id = spu.id inner JOIN kx_store_category ca on spu.cate_id = ca.id inner join kx_storage sto on s.storage_id = sto.id WHERE s.is_delete = 0
and s.storage_id = #{storageId}
and ca.id = #{categoryId}
and s.status = #{status}
and ( spu.id like CONCAT('%',#{keyword},'%') or spu.store_name like CONCAT('%',#{keyword},'%') or spu.bar_code like CONCAT('%',#{keyword},'%') )
and s.product_id not in
#{id}
order by s.storage_id,s.create_time,s.id
limit #{offset},#{size}

SELECT count(s.id) FROM kx_stock s inner JOIN kx_store_product spu on s.product_id = spu.id inner JOIN kx_store_category ca on spu.cate_id = ca.id inner join kx_storage sto on s.storage_id = sto.id WHERE s.is_delete = 0
and s.storage_id = #{storageId}
and ca.id = #{categoryId}
and ( spu.id like CONCAT('%',#{keyword},'%') or spu.store_name like CONCAT('%',#{keyword},'%') or spu.bar_code like CONCAT('%',#{keyword},'%') )
and s.status = #{status}
and spu.id not in
#{id}
